<span class="line"><span style="color:#FFCB6B;">$</span><span style="color:#A6ACCD;"> </span><span style="color:#C3E88D;">kubectl</span><span style="color:#A6ACCD;"> </span><span style="color:#C3E88D;">create</span><span style="color:#A6ACCD;"> </span><span style="color:#C3E88D;">secret</span><span style="color:#A6ACCD;"> </span><span style="color:#C3E88D;">generic</span><span style="color:#A6ACCD;"> </span><span style="color:#C3E88D;">db-user-pass</span><span style="color:#A6ACCD;"> </span><span style="color:#C3E88D;">--from-file=./username</span><span style="color:#A6ACCD;"> </span><span style="color:#C3E88D;">--from-file=./password</span></span>
